import Vue from "vue";
import VueRouter from "vue-router";
import RouterPrefetch from "vue-router-prefetch";
import DashboardPlugin from "./plugins/dashboard-plugin";
import App from "./App.vue";
import router from "./router/router";
import store from "./store";
import i18n from "./i18n";
import "./registerServiceWorker";

Vue.config.productionTip = false;

Vue.use(DashboardPlugin);
Vue.use(VueRouter);
Vue.use(RouterPrefetch);

let localAuthToken = localStorage.getItem("auth_token");
console.log(router.options.routes);
if (localAuthToken && localAuthToken !== "undefined") {
  const payload = localAuthToken.split(".")[1];
  let exp = null;

  if (payload) {
    try {
      const decoded = JSON.parse(atob(payload));
      exp = decoded.exp;
    } catch (error) {
      console.error("Error decodificando el token:", error);
      router.push({ name: "Authentication", path: "/login" });
    }
  }

  if (exp) {
    const now = Math.floor(Date.now() / 1000);
    if (now > exp) {
      console.log("El token ha expirado.");
      localStorage.removeItem("auth_token");
      router.push({ name: "Authentication", path: "/login" });
    } else {
      console.log("El token es v치lido.");
      store.dispatch("loginUserWithToken", { auth_token: localAuthToken });
    }
  } else {
    console.log("No se pudo obtener la fecha de expiraci칩n del token.");
    router.push({ name: "Authentication", path: "/login" });
  }
} else {
  console.log("No hay token en localStorage.");
  router.push({ name: "Authentication", path: "/login" });
}

new Vue({
  router,
  store,
  i18n,
  render: (h) => h(App),
}).$mount("#app");
