import axios from "axios";

export default {
  namespaced: true,
  state: {
    presignedUrl: null,
    uploadPath: null,
  },
  mutations: {
    setPresignedUrl(state, { url, path }) {
      state.presignedUrl = url;
      state.uploadPath = path;
    },
  },
  actions: {
    getPresignedUrl({ commit }, fileName) {
      const config = {
        headers: {
          Authorization: localStorage.getItem("auth_token"),
        },
      };
      return axios
        .post("http://your-backend.com/s3_presigned_url", {
          file_name: fileName,
        })
        .then((response) => {
          commit("setPresignedUrl", {
            url: response.data.url,
            path: response.data.path
          });
          return response.data;
        });
    },
    uploadFileToS3(_, { url, file }) {
      const options = {
        headers: { "Content-Type": file.type },
      };
      return axios.put(url, file, options);
    },
  },
};
