import axios from "axios";
import router from "@/router/router";

const BASE_URL = process.env.VUE_APP_BACKEND_URL;

const state = {
  residentials: [],
};

const getters = {
  getResidentials(state) {
    return state.residentials;
  },
  getResidentialById: (state) => (id) => {
    return state.residentials.find((residential) => residential.id === id);
  },
};

const actions = {
  fetchResidentials({ commit }) {
    axios
      .get(`${BASE_URL}/residentials`)
      .then((response) => {
        commit("setResidentials", response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  },
  fetchResidentialById({ commit }, id) {
    axios
      .get(`${BASE_URL}/residentials/${id}`)
      .then((response) => {
        commit("setResidentials", response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  },
  // eslint-disable-next-line no-empty-pattern
  createResidential({}, payload) {
    const config = {
      headers: {
        Authorization: state.auth_token,
      },
    };
    return new Promise((resolve, reject) => {
      axios
        .post(`${BASE_URL}/residentials`, payload)
        .then((response) => {
          state.residentials.push(response.data);
          router.push("/residentials");
          resolve(response);
        })
        .catch((error) => {
          console.error(error);
          reject(error);
        });
    });
  },
  updateResidential({ commit }, payload) {
    return new Promise((resolve, reject) => {
      axios
        .put(`${BASE_URL}/residentials/${payload.id}`, payload)
        .then((response) => {
          commit("setResidentials", response.data);
          resolve(response);
        })
        .catch((error) => {
          console.error(error);
          reject(error);
        });
    });
  },
  deleteResidential({ commit }, id) {
    return new Promise((resolve, reject) => {
      axios
        .delete(`${BASE_URL}/residentials/${id}`)
        .then((response) => {
          commit("setResidentials", response.data);
          resolve(response);
        })
        .catch((error) => {
          console.error(error);
          reject(error);
        });
    });
  },
};

const mutations = {
  setResidentials(state, residentials) {
    state.residentials = residentials;
  },
};

export default {
  state,
  getters,
  actions,
  mutations,
};
